mapper.select:
SELECT 
	  COUNT(*)
  FROM SGMS_STD_USR_LIST A
 WHERE A.GRP_ID = #{grp_id}
   AND A.USR_ID = #{usr_id}

mapper.insert:
INSERT INTO SGMS_STD_SUB_REQ(
							  GRP_ID
							, USR_ID
							, REQ_DATE
						) VALUES (
							  #{grp_id}
							, #{usr_id}
							, NOW()
								)

mapper.select:
SELECT COUNT(*)
  FROM SGMS_STD_GRP_LIST A
 WHERE GRP_MASTER = #{usr_id}

SELECT COUNT(*)
  FROM SGMS_STD_GRP_LIST A
 WHERE GRP_NAME = #{grp_name}

mapper.insert:
INSERT INTO SGMS_STD_GRP_LIST (
								  GRP_NAME
								, GRP_MASTER
								, GRP_TYPE
								, DSC
								, CRE_DATE
							) VALUES (
								  #{grp_name}
								, #{grp_master}
								, #{grp_type}
								, #{dsc}
								, NOW()
							)

mapper.select:
SELECT GRP_ID
  FROM SGMS_STD_GRP_LIST
 WHERE GRP_NAME = #{grp_name}
   AND GRP_TYPE = #{grp_type}
   AND GRP_MASTER = #{grp_master}

mapper.insert:
INSERT INTO SGMS_STD_USR_LIST (
								  GRP_ID
								, USR_ID
							) VALUES (
								  #{grp_id}
								, #{usr_id}
							)

mapper.select:
SELECT COUNT(*)
  FROM SGMS_STD_SUB_REQ
 WHERE GRP_ID = #{grp_id}
   AND USR_ID = #{usr_id}

mapper.update:
UPDATE SGMS_STD_SUB_REQ
   SET APP_DATE = NOW()
 WHERE GRP_ID = #{grp_id}
   AND USR_ID = #{usr_id}

mapper.insert:
INSERT INTO SGMS_STD_GRP_SCHD (
								  STD_YYYYMM
								, GRP_ID
								, STD_SCHD_NOTI
							) VALUES (
								  #{std_yyyymm}
								, #{grp_id}
								, NOW()
							)

mapper.select:
SELECT COUNT(*)
  FROM SGMS_STD_GRP_SCHD
 WHERE STD_YYYYMM = #{std_yyyymm}
   AND GRP_ID = #{grp_id}
