mapper.select:
SELECT COUNT(*)
  FROM SGMS_USR_LIST
 WHERE USR_ID = #{usr_id}

mapper.insert:
INSERT INTO SGMS_USR_LIST (
							  USR_ID
							, USR_PWD
							, USR_NAME
							, USR_BRTH
							, USR_PHONE
							, USR_ADDR
							, USR_LEVL
							, CRE_DATE
						) VALUES (
							  #{usr_id}
							, #{usr_pwd}
							, #{usr_name}
							, #{usr_brth}
							, #{usr_phone}
							, #{usr_addr}
							, '3'				
							, NOW()
								)

mapper.select:
SELECT 
	  USR_ID
  FROM SGMS_USR_LIST
 WHERE USR_NAME = #{usr_name}
   AND USR_BRTH = #{usr_brth}
   AND USR_PHONE = #{usr_phone}

SELECT 
	  USR_PWD
  FROM SGMS_USR_LIST
 WHERE USR_ID = #{usr_id}
   AND USR_NAME = #{usr_name}
   AND USR_BRTH = #{usr_brth}
   AND USR_PHONE = #{usr_phone}

mapper.update:
UPDATE SGMS_USR_LIST
   SET USR_PWD = #{usr_pwd}
	 , USR_BRTH = #{usr_brth}
	 , USR_PHONE = #{usr_phone}
	 , USR_ADDR = #{usr_addr}
 WHERE USR_ID = #{usr_id}
